using UnityEngine;using System.Collections; public class RotateObject : MonoBehaviour{ 	public float speed = 10f;	public bool rotateX = false;	public bool rotateY = false;	public bool rotateZ = false;	void Update ()	{		if (rotateX)			transform.Rotate (speed * Time.deltaTime, 0, 0);		if (rotateY)			transform.Rotate (0, speed * Time.deltaTime, 0);		if (rotateZ)			transform.Rotate (0, 0, speed * Time.deltaTime);	}}